import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function CustomerBookings() {
  // Theme colors
  const themeRed = '#e74c3c';
  const themeYellow = '#f39c12';
  const themeGreen = '#27ae60';
  const themeWhite = '#ecf0f1';
  const themeText = '#2c3e50';

  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Apply theme colors as CSS variables
  useEffect(() => {
    document.documentElement.style.setProperty('--theme-red', themeRed);
    document.documentElement.style.setProperty('--theme-yellow', themeYellow);
    document.documentElement.style.setProperty('--theme-green', themeGreen);
    document.documentElement.style.setProperty('--theme-white', themeWhite);
    document.documentElement.style.setProperty('--theme-text', themeText);
  }, []);

  // Fetch bookings from API
  const fetchBookings = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/customer/bookings', {
        headers: { Authorization: `Bearer ${token}` }
      });

      setBookings(response.data);
      setError(null);
    } catch (err) {
      console.error('Error fetching bookings:', err);
      setError('Failed to load bookings. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Handle booking cancellation
  const handleCancelBooking = async (bookingId) => {
    if (!window.confirm('Are you sure you want to cancel this booking?')) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/api/customer/bookings/${bookingId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Refresh bookings after cancellation
      fetchBookings();
      alert('Booking cancelled successfully');
    } catch (err) {
      console.error('Error cancelling booking:', err);
      alert('Failed to cancel booking. Please try again.');
    }
  };

  useEffect(() => {
    fetchBookings();
  }, [navigate]);

  return (
    <div style={{ maxWidth: 800, margin: "2rem auto", padding: "1rem", border: `1px solid ${themeText}`, borderRadius: 8, backgroundColor: themeWhite }}>
      <h3 style={{ textAlign: "center", color: themeText, marginBottom: "1.5rem" }}>ðŸ“‘ My Bookings</h3>

      {loading ? (
        <div style={{ textAlign: "center", padding: "2rem" }}>
          <p>Loading bookings...</p>
        </div>
      ) : error ? (
        <div style={{ textAlign: "center", padding: "2rem", color: themeRed }}>
          <p>{error}</p>
        </div>
      ) : (
        <table style={{ width: "100%", borderCollapse: "collapse", marginTop: 20, boxShadow: "0 2px 5px rgba(0,0,0,0.1)" }}>
          <thead>
            <tr style={{ background: themeGreen, color: themeWhite }}>
              <th style={{ border: `1px solid ${themeGreen}`, padding: 10 }}>Booking ID</th>
              <th style={{ border: `1px solid ${themeGreen}`, padding: 10 }}>Service</th>
              <th style={{ border: `1px solid ${themeGreen}`, padding: 10 }}>Date</th>
              <th style={{ border: `1px solid ${themeGreen}`, padding: 10 }}>Status</th>
              <th style={{ border: `1px solid ${themeGreen}`, padding: 10 }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {bookings.length > 0 ? (
              bookings.map((booking) => (
                <tr key={booking._id}>
                  <td style={{ border: "1px solid #ddd", padding: 10, color: themeText }}>{booking._id.substring(0, 8)}</td>
                  <td style={{ border: "1px solid #ddd", padding: 10, color: themeText }}>{booking.service}</td>
                  <td style={{ border: "1px solid #ddd", padding: 10, color: themeText }}>
                    {new Date(booking.date).toLocaleDateString()}
                  </td>
                  <td
                    style={{
                      border: "1px solid #ddd",
                      padding: 10,
                      color:
                        booking.status === "Confirmed" || booking.status === "Completed"
                          ? themeGreen
                          : booking.status === "Pending"
                          ? themeYellow
                          : themeRed,
                      fontWeight: "bold",
                    }}
                  >
                    {booking.status}
                  </td>
                  <td style={{ border: "1px solid #ddd", padding: 10, textAlign: "center" }}>
                    {booking.status === "Pending" && (
                      <button
                        onClick={() => handleCancelBooking(booking._id)}
                        style={{
                          backgroundColor: themeRed,
                          color: "white",
                          border: "none",
                          padding: "5px 10px",
                          borderRadius: "4px",
                          cursor: "pointer"
                        }}
                      >
                        Cancel
                      </button>
                    )}
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" style={{ textAlign: "center", padding: 20 }}>
                  No bookings found. Book a service to see your bookings here.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default CustomerBookings;
