import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header'; // Import Header component
import Footer from '../components/Footer'; // Import Footer component

function ServicesPage() {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchServices = async () => {
      try {
        setLoading(true);
        const response = await axios.get('http://localhost:5000/api/services');
        setServices(response.data);
        setError(null);
      } catch (err) {
        console.error('Error fetching services:', err);
        setError('Failed to load services. Please try again.');
      } finally {
        setLoading(false);
      }
    };
    fetchServices();
  }, []);

  // Helper function to get images based on service type
  const getServiceImages = (serviceType) => {
    switch (serviceType.toLowerCase()) {
      case 'ac repair':
      case 'ac service':
      case 'ac installation':
        return [
          { src: '/ac 1.jpeg', alt: 'AC Installation' },
          { src: '/ac 2.jpeg', alt: 'AC Repair' },
          { src: '/ac 3.jpeg', alt: 'AC Maintenance' },
          { src: '/ac 4.jpeg', alt: 'AC Cleaning' },
        ];
      case 'fridge repair':
      case 'refrigerator service':
        return [
          { src: '/fridge 1.jpeg', alt: 'Fridge Repair' },
          { src: '/fridge 2.jpeg', alt: 'Fridge Maintenance' },
          { src: '/fridge 3.jpeg', alt: 'Fridge Service' },
        ];
      case 'washing machine repair':
      case 'washing machine service':
        return [
          { src: '/wm 1.jpeg', alt: 'Washing Machine Repair' },
          { src: '/wm 2.jpeg', alt: 'Washing Machine Maintenance' },
          { src: '/wm 3.jpeg', alt: 'Washing Machine Service' },
          { src: '/wm 4.jpeg', alt: 'Washing Machine Installation' },
          { src: '/wm 5.jpeg', alt: 'Washing Machine Cleaning' },
        ];
      default:
        return [];
    }
  };

  return (
    <div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
      {/* Header */}
      <Header />

      {/* Main Content */}
      <main style={{ flex: 1, maxWidth: '1200px', margin: '0 auto', width: '100%', padding: '80px 20px 20px' }}>
        <h1 style={{ textAlign: 'center', color: 'var(--color-primary)', marginBottom: '2rem', fontSize: '2.5rem' }}>Our Services</h1>
        <p style={{ textAlign: 'center', color: 'var(--color-text)', marginBottom: '3rem', fontSize: '1.1rem' }}>
          We offer a wide range of professional appliance repair and maintenance services.
        </p>

        {loading ? (
          <div style={{ textAlign: 'center', padding: '2rem' }}>Loading services...</div>
        ) : error ? (
          <div style={{ textAlign: 'center', padding: '2rem', color: 'var(--color-error)' }}>Error: {error}</div>
        ) : services.length === 0 ? (
          <div style={{ textAlign: 'center', padding: '2rem' }}>No services available at the moment.</div>
        ) : (
          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', 
            gap: '25px' 
          }}>
            {services.map(service => (
              <div 
                key={service._id} 
                style={{ 
                  backgroundColor: 'var(--color-white)', 
                  borderRadius: '12px', 
                  boxShadow: 'var(--shadow)', 
                  overflow: 'hidden',
                  display: 'flex',
                  flexDirection: 'column',
                  transition: 'transform 0.3s ease, box-shadow 0.3s ease',
                  border: '1px solid var(--border)'
                }}
                onMouseOver={(e) => {
                  e.currentTarget.style.transform = 'translateY(-5px)';
                  e.currentTarget.style.boxShadow = '0 8px 16px rgba(0,0,0,0.15)';
                }}
                onMouseOut={(e) => {
                  e.currentTarget.style.transform = 'translateY(0)';
                  e.currentTarget.style.boxShadow = 'var(--shadow)';
                }}
              >
                {getServiceImages(service.type).length > 0 && (
                  <div style={{ height: '200px', overflow: 'hidden' }}>
                    <img 
                      src={getServiceImages(service.type)[0].src} 
                      alt={service.type} 
                      style={{ 
                        width: '100%', 
                        height: '100%', 
                        objectFit: 'cover', 
                        borderBottom: '1px solid var(--border)'
                      }} 
                    />
                  </div>
                )}
                <div style={{ padding: '20px', flexGrow: 1 }}>
                  <h2 style={{ color: 'var(--color-secondary)', marginBottom: '10px', fontSize: '1.8rem' }}>{service.type}</h2>
                  <p style={{ fontSize: '1rem', color: 'var(--color-text)', marginBottom: '15px', lineHeight: '1.5' }}>{service.description}</p>
                  <div style={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    alignItems: 'center', 
                    marginTop: 'auto',
                    paddingTop: '15px',
                    borderTop: '1px solid var(--border)'
                  }}>
                    <p style={{ fontSize: '1.2rem', color: 'var(--color-primary)', fontWeight: 'bold', margin: 0 }}>
                      Price: ₹{service.price.toFixed(2)}
                    </p>
                    <Link to="/login" style={{ textDecoration: 'none' }}>
                      <button style={{ 
                        padding: '8px 15px', 
                        background: 'var(--color-accent)', 
                        color: 'var(--color-white)', 
                        border: 'none', 
                        borderRadius: '6px', 
                        fontSize: '0.9rem', 
                        cursor: 'pointer',
                        transition: 'background-color 0.3s ease'
                      }}
                      onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#e68a00'}
                      onMouseOut={(e) => e.currentTarget.style.backgroundColor = 'var(--color-accent)'}
                      >
                        Book Now
                      </button>
                    </Link>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        <div style={{ textAlign: 'center', margin: '4rem 0 2rem' }}>
          <Link to="/">
            <button style={{ 
              padding: '12px 28px', 
              background: 'var(--color-primary)', 
              color: 'var(--color-white)', 
              border: 'none', 
              borderRadius: '8px', 
              fontSize: '1.1rem', 
              cursor: 'pointer',
              fontWeight: 'bold',
              transition: 'background-color 0.3s ease'
            }}
            onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#15803d'}
            onMouseOut={(e) => e.currentTarget.style.backgroundColor = 'var(--color-primary)'}
            >
              Back to Home
            </button>
          </Link>
        </div>
      </main>

      {/* Footer */}
      <Footer />
    </div>
  );
}

export default ServicesPage;
