import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function CustomerBookings() {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Fetch bookings from API
  const fetchBookings = async () => {
    try {
      const token = sessionStorage.getItem('token'); // Changed from localStorage
      if (!token) {
        navigate('/login');
        return;
      }

      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/customer/bookings', {
        headers: { Authorization: `Bearer ${token}` }
      });

      setBookings(response.data);
      setError(null);
    } catch (err) {
      console.error('Error fetching bookings:', err);
      setError('Failed to load bookings. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Handle booking cancellation
  const handleCancelBooking = async (bookingId) => {
    if (!window.confirm('Are you sure you want to cancel this booking?')) {
      return;
    }

    try {
      const token = sessionStorage.getItem('token'); // Changed from localStorage
      await axios.delete(`http://localhost:5000/api/customer/bookings/${bookingId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Refresh bookings after cancellation
      fetchBookings();
      alert('Booking cancelled successfully');
    } catch (err) {
      console.error('Error cancelling booking:', err);
      alert('Failed to cancel booking. Please try again.');
    }
  };

  useEffect(() => {
    fetchBookings(); // Initial fetch

    const intervalId = setInterval(fetchBookings, 5000); // Poll every 5 seconds

    return () => clearInterval(intervalId); // Clean up interval on component unmount
  }, [navigate]);

  return (
    <div className="card" style={{ maxWidth: 800, margin: "2rem auto" }}>
      <h3 style={{ textAlign: "center", color: 'var(--color-text)', marginBottom: "1.5rem" }}>ðŸ“‘ My Bookings</h3>

      {loading ? (
        <div style={{ textAlign: "center", padding: "2rem" }}>
          <p>Loading bookings...</p>
        </div>
      ) : error ? (
        <div style={{ textAlign: "center", padding: "2rem", color: 'var(--color-error)' }}>
          <p>{error}</p>
        </div>
      ) : (
        <table className="data-table">
          <thead>
            <tr>
              <th>Booking ID</th>
              <th>Service</th>
              <th>Price</th>
              <th>Date</th>
              <th>Status</th>
              <th>Assigned Employee</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {bookings.length > 0 ? (
              bookings.map((booking) => (
                <tr key={booking._id}>
                  <td>{booking._id.substring(0, 8)}</td>
                  <td>{booking.service?.type || 'N/A'}</td>
                  <td>â‚¹{booking.service?.price || 'N/A'}</td>
                  <td>
                    {new Date(booking.date).toLocaleDateString()}
                  </td>
                  <td>
                    <span className={`status-badge status-${booking.status.toLowerCase().replace(' ', '-')}`}>
                      {booking.status}
                    </span>
                  </td>
                  <td>
                    {booking.employee ? (
                      <>
                        {booking.employee.profile?.name || 'N/A'}
                        <br />
                        <a href={`tel:${booking.employee.phone}`} style={{ textDecoration: 'none', color: 'var(--color-secondary)' }}>
                          {booking.employee.phone}
                        </a>
                        <br />
                        <small>{booking.employee.profile?.address || ''}</small>
                      </>
                    ) : 'Not Assigned'}
                  </td>
                  <td>
                    {booking.status === "Pending" && (
                      <button
                        onClick={() => handleCancelBooking(booking._id)}
                        className="btn btn-danger btn-sm"
                      >
                        Cancel
                      </button>
                    )}
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7" style={{ textAlign: "center", padding: 20 }}>
                  No bookings found. Book a service to see your bookings here.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default CustomerBookings;
