import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import StarRating from '../../components/StarRating'; // Import the new StarRating component
import '../../src/styles/ReviewsPage.css'; // Import the updated CSS

function CustomerReviews() {
  const [myReviews, setMyReviews] = useState([]);
  const [eligibleBookings, setEligibleBookings] = useState([]);
  const [selectedBooking, setSelectedBooking] = useState('');
  const [reviewComment, setReviewComment] = useState('');
  const [rating, setRating] = useState(0); // Numeric rating 1-5
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const navigate = useNavigate();

  const fetchReviewsAndBookings = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      // Fetch customer's own reviews
      const reviewsResponse = await axios.get('http://localhost:5000/api/reviews', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMyReviews(reviewsResponse.data);

      // Fetch customer's bookings
      const bookingsResponse = await axios.get('http://localhost:5000/api/customer/bookings', {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Filter for completed bookings that haven't been reviewed yet
      const reviewedBookingIds = new Set(reviewsResponse.data.map(review => review.booking?._id || review.booking));
      const eligible = bookingsResponse.data.filter(booking => 
        booking.status === 'Completed' && !reviewedBookingIds.has(booking._id)
      );
      setEligibleBookings(eligible);

      setError('');
    } catch (err) {
      console.error('Error fetching data:', err);
      setError(err.response?.data?.message || 'Failed to load reviews or bookings. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchReviewsAndBookings();
  }, [navigate]);

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    if (!selectedBooking || !reviewComment || rating === 0) {
      setError('Please select a booking, provide a rating, and write a comment.');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5000/api/reviews', 
        { booking: selectedBooking, rating, comment: reviewComment },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSuccess('Review submitted successfully!');

      // Reset form fields
      setSelectedBooking('');
      setReviewComment('');
      setRating(0);

      // Re-fetch data to update lists
      fetchReviewsAndBookings();
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      console.error('Error submitting review:', err);
      setError(err.response?.data?.message || 'Failed to submit review. Please try again.');
    }
  };

  if (loading) return <p className="loading">Loading reviews...</p>;

  return (
    <div className="card submit-review-section" style={{ maxWidth: "800px", margin: "2rem auto" }}>
      <h3 style={{ color: 'var(--color-text)' }}>⭐ My Reviews</h3>
      <p style={{ color: 'var(--color-text)' }}>Submit your feedback for completed bookings below.</p>

      {error && (
        <div className="status-message error">
          {error}
        </div>
      )}
      {success && (
        <div className="status-message success">
          {success}
        </div>
      )}

      {/* Review Form */}
      <form onSubmit={handleSubmitReview} style={{ marginBottom: "20px" }}>
        <div className="form-group">
          <label style={{ color: 'var(--color-text)' }}>
            Select Completed Booking to Review:
          </label>
          <select
            value={selectedBooking}
            onChange={(e) => setSelectedBooking(e.target.value)}
            className="form-control"
            required
          >
            <option value="">-- Select a booking --</option>
            {eligibleBookings.length === 0 ? (
              <option disabled>No eligible bookings to review.</option>
            ) : (
              eligibleBookings.map(booking => (
                <option key={booking._id} value={booking._id}>
                  {booking.service?.type || 'Unknown Service'} on {new Date(booking.date).toLocaleDateString()}
                </option>
              ))
            )}
          </select>
        </div>

        <div className="form-group">
          <label style={{ color: 'var(--color-text)' }}>
            Rating:
          </label>
          <div className="rating-input">
            <StarRating rating={rating} editable={true} onRatingChange={setRating} />
          </div>
        </div>

        <div className="form-group">
          <label style={{ color: 'var(--color-text)' }}>
            Your Comment:
          </label>
          <textarea
            value={reviewComment}
            onChange={(e) => setReviewComment(e.target.value)}
            placeholder="Write your review..."
            rows="4"
            className="form-control"
            required
          ></textarea>
        </div>

        <button
          type="submit"
          className="btn btn-primary"
        >
          Submit Review
        </button>
      </form>

      {/* Display Submitted Reviews */}
      <div>
        <h4 style={{ color: 'var(--color-text)', marginBottom: "1rem" }}>My Submitted Reviews</h4>
        {myReviews.length === 0 ? (
          <p>No reviews submitted yet.</p>
        ) : (
          myReviews.map((r) => (
            <div
              key={r._id}
              className="review-card"
            >
              <p style={{ margin: "5px 0" }}>{r.comment}</p>
              <p style={{ margin: "5px 0", fontWeight: "bold" }}>
                Rating: <StarRating rating={r.rating} />
              </p>
              <p style={{ margin: "5px 0", fontSize: "0.8rem", color: 'var(--color-text)' }}>
                For Booking: {r.booking?.service?.type || 'N/A'} - {new Date(r.createdAt).toLocaleDateString()}
              </p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default CustomerReviews;
