import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header'; // Import Header component
import Footer from '../components/Footer'; // Import Footer component

function ServicesPage() {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchServices = async () => {
      try {
        setLoading(true);
        const response = await axios.get('http://localhost:5000/api/services');
        setServices(response.data);
        setError(null);
      } catch (err) {
        console.error('Error fetching services:', err);
        setError('Failed to load services. Please try again.');
      } finally {
        setLoading(false);
      }
    };
    fetchServices();
  }, []);

  return (
    <div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
      {/* Header */}
      <Header />

      {/* Main Content */}
      <main style={{ flex: 1, maxWidth: '900px', margin: '0 auto', width: '100%', paddingTop: '80px' }}>
        <h1 style={{ textAlign: 'center', color: 'var(--color-text)', marginBottom: '2rem' }}>Our Services</h1>

        {loading ? (
          <div style={{ textAlign: 'center', padding: '2rem' }}>Loading services...</div>
        ) : error ? (
          <div style={{ textAlign: 'center', padding: '2rem', color: 'var(--color-error)' }}>Error: {error}</div>
        ) : services.length === 0 ? (
          <div style={{ textAlign: 'center', padding: '2rem' }}>No services available at the moment.</div>
        ) : (
          services.map(service => (
            <div key={service._id} style={{ marginBottom: '3rem', background: 'var(--color-white)', padding: '1.5rem', borderRadius: '10px', boxShadow: '0 2px 10px rgba(0,0,0,0.05)', border: `1px solid var(--border)` }}>
              <h2 style={{ color: 'var(--color-error)', marginBottom: '1rem' }}>{service.type}</h2>
              <p style={{ fontSize: '1.1rem', color: 'var(--color-text)', marginBottom: '1.5rem' }}>{service.description}</p>
              <p style={{ fontSize: '1.1rem', color: 'var(--color-primary)', fontWeight: 'bold', marginBottom: '1.5rem' }}>Price: â‚¹{service.price}</p>

              {service.imageUrl && (
                <div style={{ textAlign: 'center', marginBottom: '1rem' }}>
                  <img 
                    src={service.imageUrl} 
                    alt={service.type} 
                    style={{ 
                      width: '250px', 
                      height: '250px', 
                      objectFit: 'cover', 
                      borderRadius: '8px',
                      boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
                    }} 
                  />
                  <p style={{ marginTop: '0.5rem', color: 'var(--color-text)' }}>{service.category} Service</p>
                </div>
              )}
            </div>
          ))
        )}

        <div style={{ textAlign: 'center', margin: '2rem 0' }}>
          <Link to="/">
            <button style={{ padding: '0.75rem 2rem', background: 'var(--color-primary)', color: 'var(--color-white)', border: 'none', borderRadius: '4px', fontSize: '1.1rem', cursor: 'pointer' }}>
              Back to Home
            </button>
          </Link>
        </div>
      </main>

      {/* Footer */}
      <Footer />
    </div>
  );
}

export default ServicesPage;
