import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function EmployeeStatus() {
  const [isWorking, setIsWorking] = useState(false);
  const [currentAssignment, setCurrentAssignment] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchEmployeeStatus();
  }, []);

  const fetchEmployeeStatus = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const response = await axios.get('http://localhost:5000/api/employee/status', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setIsWorking(response.data.isWorking);
      setCurrentAssignment(response.data.currentAssignment);
      setError('');
    } catch (err) {
      console.error('Error fetching employee status:', err);
      setError(err.response?.data?.message || 'Failed to load status. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleToggleWorkingStatus = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const newStatus = !isWorking;
      await axios.put('http://localhost:5000/api/employee/status/working', 
        { isWorking: newStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setIsWorking(newStatus);
      setSuccess(`Status updated to ${newStatus ? 'Working' : 'Free'} âœ…`);
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      console.error('Error updating working status:', err);
      setError(err.response?.data?.message || 'Failed to update status. Please try again.');
      setTimeout(() => setError(''), 3000);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleDateString(undefined, {
      year: "numeric",
      month: "short",
      day: "numeric",
    });
  };

  if (loading) {
    return <p className="text-center">Loading employee status...</p>;
  }

  return (
    <div className="card" style={{ maxWidth: 800, margin: "2rem auto" }}>
      <h3 style={{ marginBottom: "1.5rem", textAlign: "center", color: 'var(--color-text)' }}>My Status</h3>

      {error && (
        <div className="status-message error">
          {error}
        </div>
      )}
      {success && (
        <div className="status-message success">
          {success}
        </div>
      )}

      <div className="form-group">
        <label>Current Working Status:</label>
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <span className={`status-badge ${isWorking ? 'status-success' : 'status-pending'}`}>
            {isWorking ? 'Working' : 'Free'}
          </span>
          <button
            onClick={handleToggleWorkingStatus}
            className={`btn ${isWorking ? 'btn-danger' : 'btn-success'} btn-sm`}
          >
            Toggle to {isWorking ? 'Free' : 'Working'}
          </button>
        </div>
      </div>

      <div className="form-group" style={{ marginTop: '20px' }}>
        <label>Current Assignment:</label>
        {currentAssignment ? (
          <div className="card bg-light" style={{ padding: '15px', borderRadius: '8px' }}>
            <p><strong>Service:</strong> {currentAssignment.service?.type || 'N/A'}</p>
            <p><strong>Date:</strong> {formatDate(currentAssignment.date)}</p>
            <p><strong>Time:</strong> {currentAssignment.time || 'N/A'}</p>
            <p><strong>Customer:</strong> {currentAssignment.user?.profile?.name || 'N/A'}</p>
            <p><strong>Customer Phone:</strong> <a href={`tel:${currentAssignment.user?.phone}`} className="text-secondary">{currentAssignment.user?.phone || 'N/A'}</a></p>
          </div>
        ) : (
          <p>No current assignment.</p>
        )}
      </div>
    </div>
  );
}

export default EmployeeStatus;
