import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header'; // Import Header component
import Footer from '../components/Footer'; // Import Footer component

function HomePage() {
  const [showBookingForm, setShowBookingForm] = useState(false);
  const [customerProfile, setCustomerProfile] = useState(null); // To store logged-in user's profile
  const [availableServices, setAvailableServices] = useState([]); // To store services from backend
  const [bookingData, setBookingData] = useState({
    service: '',
    date: '',
    time: '',
    address: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const isLoggedIn = !!localStorage.getItem('token');

  // Fetch customer profile if logged in
  useEffect(() => {
    const fetchProfile = async () => {
      if (isLoggedIn) {
        try {
          const token = localStorage.getItem('token');
          const response = await axios.get('http://localhost:5000/api/customer/profile', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setCustomerProfile(response.data);
        } catch (err) {
          console.error('Error fetching customer profile:', err);
          // If token is invalid or expired, log out
          localStorage.removeItem('token');
          navigate('/login');
        }
      }
    };
    fetchProfile();
  }, [isLoggedIn, navigate]);

  // Fetch available services
  useEffect(() => {
    const fetchServices = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/services');
        setAvailableServices(response.data);
      } catch (err) {
        console.error('Error fetching services:', err);
        setError('Failed to load services. Please try again.');
      }
    };
    fetchServices();
  }, []);

  // Handle Book Now button click
  const handleBookNow = () => {
    if (!isLoggedIn) {
      navigate('/register');
    } else {
      setShowBookingForm(true);
      setError('');
      setSuccess('');
      // Pre-fill address if available from profile
      if (customerProfile?.profile?.address) {
        setBookingData(prev => ({ ...prev, address: customerProfile.profile.address }));
      }
    }
  };

  // Handle booking form input changes
  const handleBookingChange = (e) => {
    setBookingData({ ...bookingData, [e.target.name]: e.target.value });
  };

  // Booking form submit
  const handleBookingSubmit = async (e) => {
    e.preventDefault();
    if (!bookingData.service || !bookingData.date || !bookingData.time || !bookingData.address) {
      setError('Please fill all required fields.');
      setSuccess('');
      return;
    }

    setIsLoading(true);
    setError('');
    setSuccess('');

    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5000/api/customer/bookings', bookingData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuccess('Booking submitted successfully! Our team will contact you shortly.');

      // Reset form
      setBookingData({
        service: '',
        date: '',
        time: '',
        address: '',
      });
    } catch (err) {
      console.error('Booking submission failed:', err);
      setError(err.response?.data?.message || 'Failed to submit booking. Please try again.');
    } finally {
      setIsLoading(false);
      setTimeout(() => setShowBookingForm(false), 3000);
    }
  };

  return (
    <div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
      {/* Header */}
      <Header />

      {/* Main Content */}
      <main style={{ flex: 1, maxWidth: '900px', margin: '0 auto', width: '100%', paddingTop: '100px' }}> {/* Adjusted paddingTop */}
        <section style={{ display: 'flex', gap: '1rem', marginBottom: '1rem' }}>
          <a href="https://wa.me/9966972228" style={{ color: 'var(--color-primary)', fontWeight: 'bold', textDecoration: 'none' }}>Whatsapp Us</a>
