import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

function RegisterPage() {
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const initialValues = {
    name: "",
    phone: "",
    password: "",
    confirmPassword: "",
    location: "",
  };

  const validationSchema = Yup.object().shape({
    name: Yup.string().required("Name is required"),
    phone: Yup.string()
      .matches(/^[0-9]{10}$/, "Phone number must be a 10-digit number")
      .required("Phone number is required"),
    password: Yup.string()
      .min(6, "Password must be at least 6 characters")
      .required("Password is required"),
    confirmPassword: Yup.string()
      .oneOf([Yup.ref("password"), null], "Passwords must match")
      .required("Confirm Password is required"),
    location: Yup.string()
      .required("District is required")
      .notOneOf(["Other"], "Registration not available for this district."),
  });

  const handleRegister = async (values, { setSubmitting }) => {
    setError("");
    setSuccess("");
    setLoading(true);

    try {
      const registerRes = await axios.post("http://localhost:5000/api/auth/register", {
        name: values.name,
        phone: values.phone,
        password: values.password,
      });

      if (registerRes.status === 201) {
        localStorage.setItem("token", registerRes.data.token);
        if (registerRes.data.user.role) {
          localStorage.setItem("role", registerRes.data.user.role);
        }

        setSuccess("Registration successful! ðŸŽ‰");
        setTimeout(() => navigate("/customer/profile"), 2000);
      }
    } catch (err) {
      console.error("Registration error:", err);
      if (err.response) {
        // Server responded with a status other than 2xx
        setError(err.response.data.message || "Registration failed. Please try again.");
      } else if (err.request) {
        // Request was made but no response was received
        setError("No response from server. Please ensure the backend is running and accessible.");
      } else {
        // Something else happened while setting up the request
        setError("An unexpected error occurred. Please try again.");
      }
    } finally {
      setLoading(false);
      setSubmitting(false);
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: "40px auto", padding: 24, border: `1px solid var(--border)`, borderRadius: 'var(--radius)', boxShadow: 'var(--shadow)', backgroundColor: 'var(--card)' }}>
      <h2 style={{ textAlign: "center", marginBottom: 16, color: 'var(--color-primary)' }}>Register</h2>
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={handleRegister}
      >
        {({ isSubmitting, values }) => (
          <Form>
            <div style={{ marginBottom: 12 }}>
              <Field
                type="text"
                name="name"
                placeholder="Name *"
                style={{ width: "100%", padding: 8, border: `1px solid var(--border)`, borderRadius: '4px' }}
                disabled={isSubmitting || loading}
              />
              <ErrorMessage name="name" component="div" style={{ color: 'var(--color-error)', fontSize: '0.85rem', marginTop: '4px' }} />
            </div>
            <div style={{ marginBottom: 12 }}>
              <Field
                type="tel"
                name="phone"
                placeholder="Phone Number *"
                pattern="[0-9]{10}"
                title="Enter a 10-digit phone number"
                style={{ width: "100%", padding: 8, border: `1px solid var(--border)`, borderRadius: '4px' }}
                disabled={isSubmitting || loading}
              />
              <ErrorMessage name="phone" component="div" style={{ color: 'var(--color-error)', fontSize: '0.85rem', marginTop: '4px' }} />
            </div>
            <div style={{ marginBottom: 12 }}>
              <Field
                type="password"
                name="password"
                placeholder="Password *"
                style={{ width: "100%", padding: 8, border: `1px solid var(--border)`, borderRadius: '4px' }}
                disabled={isSubmitting || loading}
              />
              <ErrorMessage name="password" component="div" style={{ color: 'var(--color-error)', fontSize: '0.85rem', marginTop: '4px' }} />
            </div>
            <div style={{ marginBottom: 12 }}>
              <Field
                type="password"
                name="confirmPassword"
                placeholder="Confirm Password *"
                style={{ width: "100%", padding: 8, border: `1px solid var(--border)`, borderRadius: '4px' }}
                disabled={isSubmitting || loading}
              />
              <ErrorMessage name="confirmPassword" component="div" style={{ color: 'var(--color-error)', fontSize: '0.85rem', marginTop: '4px' }} />
            </div>
            <div style={{ marginBottom: 12 }}>
              <Field
                as="select"
                name="location"
                style={{ width: "100%", padding: 8, border: `1px solid var(--border)`, borderRadius: '4px' }}
                disabled={isSubmitting || loading}
              >
                <option value="">Select District</option>
                <option value="Guntur">Guntur</option>
                <option value="Prakasam">Prakasam</option>
                <option value="Palanadu">Palanadu</option>
                <option value="Other">Other District</option>
              </Field>
              <ErrorMessage name="location" component="div" style={{ color: 'var(--color-error)', fontSize: '0.85rem', marginTop: '4px' }} />
            </div>

            {values.location === "Other" && (
              <div style={{ color: "var(--color-error)", marginBottom: 12 }}>
                Service available only in Guntur, Prakasam & Palanadu.
                Please call us directly at <b>+91-9266856088</b> or <b>+91-9910922088</b>.
              </div>
            )}

            <button
              type="submit"
              style={{
                width: "100%",
                padding: 10,
                background: (isSubmitting || loading || values.location === "Other") ? 'var(--color-textLight)' : 'var(--color-accent)',
                color: 'var(--color-white)',
                border: "none",
                borderRadius: 4,
                cursor: (isSubmitting || loading || values.location === "Other") ? "not-allowed" : "pointer",
                opacity: (isSubmitting || loading || values.location === "Other") ? 0.7 : 1
              }}
              disabled={isSubmitting || loading || values.location === "Other"}
            >
              {(isSubmitting || loading) ? "Processing..." : "Register"}
            </button>
          </Form>
        )}
      </Formik>

      {error && <div style={{ color: 'var(--color-error)', marginTop: 12 }}>{error}</div>}
      {success && <div style={{ color: 'var(--color-success)', marginTop: 12 }}>{success}</div>}

      <div style={{ marginTop: 20, textAlign: "center" }}>
        <p style={{ color: 'var(--color-text)' }}>
          Already have an account?{" "}
          <Link to="/login" style={{ color: 'var(--color-primary)', fontWeight: "bold", textDecoration: "none" }}>
            Login here
          </Link>
        </p>
      </div>
    </div>
  );
}

export default RegisterPage;
