import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header'; // Import the Header component
import Footer from '../components/Footer'; // Import the Footer component

function ServicesPage() {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchServices = async () => {
      try {
        setLoading(true);
        const response = await axios.get('http://localhost:5000/api/services');
        setServices(response.data);
        setLoading(false);
        setError('');
      } catch (error) {
        console.error('Error fetching services:', error);
        setError(error.response?.data?.message || 'Failed to load services. Please try again.');
        setLoading(false);
      }
    };

    fetchServices();
  }, []);

  if (loading) {
    return (
      <div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', paddingTop: '80px' }}>
        <p>Loading services...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', paddingTop: '80px' }}>
        <p style={{ color: 'var(--color-error)' }}>Error: {error}</p>
      </div>
    );
  }

  return (
    <div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
      <Header /> {/* Use the imported Header component */}

      <main style={{ flex: 1, maxWidth: '900px', margin: '0 auto', width: '100%', paddingTop: '80px', padding: '20px' }}>
        <h1 style={{ textAlign: 'center', color: 'var(--color-text)', marginBottom: '2rem' }}>Our Services</h1>

        {services.length === 0 ? (
          <p style={{ textAlign: 'center', color: 'var(--color-textLight)' }}>No services available at the moment. Please check back later!</p>
        ) : (
          services.map(service => (
            <div key={service._id} style={{ marginBottom: '3rem', background: 'var(--color-white)', padding: '1.5rem', borderRadius: '10px', boxShadow: 'var(--shadow)', border: '1px solid var(--border)' }}>
              <h2 style={{ color: 'var(--color-primary)', marginBottom: '1rem' }}>{service.type}</h2>
              <p style={{ fontSize: '1.1rem', color: 'var(--color-text)', marginBottom: '1.5rem', lineHeight: '1.6' }}>{service.description}</p>

              {service.imageUrl && (
                <div style={{ textAlign: 'center', marginBottom: '1.5rem' }}>
                  <img 
                    src={service.imageUrl} 
                    alt={service.type} 
                    style={{ 
                      maxWidth: '100%', 
                      height: 'auto', 
                      maxHeight: '300px',
                      objectFit: 'cover', 
                      borderRadius: '8px',
                      boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
                    }} 
                  />
                </div>
              )}
              <p style={{ fontSize: '0.9rem', color: 'var(--color-textLight)', textAlign: 'right' }}>Category: {service.category}</p>
            </div>
          ))
        )}

        <div style={{ textAlign: 'center', margin: '2rem 0' }}>
          <Link to="/">
            <button style={{ padding: '0.75rem 2rem', background: 'var(--color-primary)', color: 'var(--color-white)', border: 'none', borderRadius: '4px', fontSize: '1.1rem', cursor: 'pointer' }}>
              Back to Home
            </button>
          </Link>
        </div>
      </main>

      <Footer />
    </div>
  );
}

export default ServicesPage;
