import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header'; // Import Header component
import Footer from '../components/Footer'; // Import Footer component

const districts = ["Guntur", "Palnadu", "Prakasam"];

function HomePage() {
  const [showBookingForm, setShowBookingForm] = useState(false);
  const [customerProfile, setCustomerProfile] = useState(null); // To store logged-in user's profile
  const [availableServices, setAvailableServices] = useState([]); // To store services from backend
  const [bookingData, setBookingData] = useState({
    service: '', // Stores service ID
    serviceName: '', // Stores service name for display
    date: '',
    time: '',
    address: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const isLoggedIn = !!sessionStorage.getItem('token');

  // Fetch customer profile if logged in
  useEffect(() => {
    const fetchProfile = async () => {
      if (isLoggedIn) {
        try {
          const token = sessionStorage.getItem('token');
          const response = await axios.get('http://localhost:5000/api/customer/profile', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setCustomerProfile(response.data);
        } catch (err) {
          console.error('Error fetching customer profile:', err);
          sessionStorage.removeItem('token');
          navigate('/login');
        }
      }
    };
    fetchProfile();
  }, [isLoggedIn, navigate]);

  // Fetch available services
  useEffect(() => {
    const fetchServices = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/services');
        setAvailableServices(response.data);
      } catch (err) {
        console.error('Error fetching services:', err);
        setError('Failed to load services. Please try again.');
      }
    };
    fetchServices();
  }, []);

  // Handle Book Now button click
  const handleBookNow = (serviceId = '', serviceName = '') => {
    if (!isLoggedIn) {
      navigate('/register');
    } else {
      setShowBookingForm(true);
      setError('');
      setSuccess('');
      setBookingData(prev => ({ 
        ...prev, 
        service: serviceId, 
        serviceName: serviceName,
        address: customerProfile?.profile?.address || '' // Pre-fill address if available
      }));
    }
  };

  // Handle booking form input changes
  const handleBookingChange = (e) => {
    const { name, value } = e.target;
    if (name === 'service') {
      const selectedService = availableServices.find(s => s._id === value);
      setBookingData(prev => ({ 
        ...prev, 
        service: value, 
        serviceName: selectedService ? selectedService.type : '' 
      }));
    } else {
      setBookingData({ ...bookingData, [name]: value });
    }
  };

  // Booking form submit
  const handleBookingSubmit = async (e) => {
    e.preventDefault();
    if (!bookingData.service || !bookingData.date || !bookingData.time || !bookingData.address) {
      setError('Please fill all required fields.');
      setSuccess('');
      return;
    }

    setIsLoading(true);
    setError('');
    setSuccess('');

    try {
      const token = sessionStorage.getItem('token');
      await axios.post('http://localhost:5000/api/customer/bookings', {
        service: bookingData.service,
        date: bookingData.date,
        time: bookingData.time,
        address: bookingData.address,
        problemDescription: 'General booking from homepage' // Default problem description for homepage bookings
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuccess('Booking submitted successfully! Our team will contact you shortly.');

      // Reset form
      setBookingData({
        service: '',
        serviceName: '',
        date: '',
        time: '',
        address: '',
      });
    } catch (err) {
      console.error('Booking submission failed:', err);
      setError(err.response?.data?.message || 'Failed to submit booking. Please try again.');
    } finally {
      setIsLoading(false);
      setTimeout(() => setShowBookingForm(false), 3000);
    }
  };

  // Helper to get service details by type (case-insensitive)
  const getServiceByType = (type) => {
    return availableServices.find(s => s.type.toLowerCase().includes(type.toLowerCase()));
  };

  const acService = getServiceByType('ac');
  const fridgeService = getServiceByType('fridge');
  const wmService = getServiceByType('washing machine');
  const electricalService = getServiceByType('electrical'); // Assuming an electrical service exists

  return (
    <div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
      {/* Header */}
      <Header />
      {/* Added padding to main content to account for fixed header */}
      <div style={{ paddingTop: '100px' }}> {/* Increased padding to 100px for more clearance */}
        {/* Whatsapp and Call Now buttons section */}
        <section style={{ display: 'flex', gap: '1rem', marginBottom: '2rem', justifyContent: 'center', maxWidth: '900px', margin: '0 auto', width: '100%', paddingLeft: '20px', paddingRight: '20px' }}>
            <a href="https://wa.me/9966972228" style={{ textDecoration: 'none' }}>
              <button className="btn btn-success"> {/* Using global CSS classes */}
                Whatsapp Us
              </button>
            </a>
            <a href="tel:9966972228" style={{ textDecoration: 'none' }}>
              <button className="btn btn-danger"> {/* Using global CSS classes */}
                Call Now
              </button>
            </a>
          </section>

        {/* Main Content */}
        <main style={{ flex: 1, maxWidth: '900px', margin: '0 auto', width: '100%', padding: '20px' }}>


          {/* Booking Form */}
          {showBookingForm && (
            <div className="card" style={{ maxWidth: '600px', margin: '0 auto 2rem' }}> {/* Reusing 'card' class */}
              <h2 style={{ textAlign: 'center', marginBottom: '20px', color: 'var(--color-primary)' }}>Book Your Service</h2>
              {error && <div className="status-message error">{error}</div>}
              {success && <div className="status-message success">{success}</div>}

              <form onSubmit={handleBookingSubmit}>
                <div className="form-group"> {/* Reusing 'form-group' class */}
                  <label>Service:</label>
                  <select 
                    name="service"
                    value={bookingData.service} 
                    onChange={handleBookingChange}
                    className="form-control" 
                    required
                  >
                    <option value="">Select Service</option>
                    {availableServices.map((service) => (
                      <option key={service._id} value={service._id}>{service.type}</option>
                    ))}
                  </select>
                </div>

                <div className="form-group">
                  <label>Date:</label>
                  <input 
                    type="date" 
                    name="date"
                    value={bookingData.date} 
                    onChange={handleBookingChange}
                    className="form-control"
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Time:</label>
                  <input 
                    type="time" 
                    name="time"
                    value={bookingData.time} 
                    onChange={handleBookingChange}
                    className="form-control"
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Address:</label>
                  <textarea 
                    name="address"
                    value={bookingData.address} 
                    onChange={handleBookingChange}
                    className="form-control"
                    rows="3" // Added rows for better textarea sizing
                    required
                  ></textarea>
                </div>

                <button 
                  type="submit" 
                  disabled={isLoading}
                  className={`btn ${isLoading ? 'btn-disabled' : 'btn-secondary'}`} // Using global CSS classes
                >
                  {isLoading ? 'Processing...' : 'Submit Booking'}
                </button>
              </form>
            </div>
          )}

          {/* Service Sections - Dynamically rendered */}
          {[acService, fridgeService, wmService, electricalService].filter(Boolean).map((service, index) => (
            <section key={service._id || index} className="card" style={{ marginBottom: '3rem' }}> {/* Reusing 'card' class */}
              <h2 style={{ color: 'var(--color-primary)', fontSize: '2rem', marginBottom: '1rem', textAlign: 'center' }}>{service.type}</h2>
              <p style={{ fontSize: '1.1rem', color: 'var(--color-text)', marginBottom: '1.5rem', lineHeight: '1.6' }}>
                {service.description}
              </p>
              {service.imageUrl && (
                <div style={{ textAlign: 'center', marginBottom: '1.5rem' }}>
                  <img 
                    src={service.imageUrl} 
                    alt={service.type} 
                    style={{ 
                      maxWidth: '100%', 
                      height: 'auto', 
                      maxHeight: '300px',
                      objectFit: 'cover', 
                      borderRadius: '8px',
                      boxShadow: 'var(--shadow)'
                    }} 
                  />
                </div>
              )}
            </section>
          ))}

          <section className="card" style={{ textAlign: 'center', marginTop: '4rem' }}> {/* Reusing 'card' class */}
            <div style={{ fontSize: '1.5rem', color: 'var(--color-accent)', fontWeight: 'bold', marginBottom: '0.5rem' }}>5.0 / 5.0 Google reviews</div>
            <div style={{ marginBottom: '0.5rem', fontSize: '1.1rem', color: 'var(--color-text)' }}>
              <span style={{ fontWeight: 'bold' }}>Hours:</span> Open 24 hours
            </div>
            <div style={{ marginBottom: '0.5rem', fontSize: '1.1rem', color: 'var(--color-text)' }}>
              <span style={{ fontWeight: 'bold' }}>Contact Number:</span> 9966972228
            </div>
          </section>
        </main>
      </div>
      {/* Footer */}
      <Footer />
    </div>
  );
}

export default HomePage;
