import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../utils/api'; // Import the new API utility


function LoginPage() {
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    if (!phone || !password) {
      setError('Please fill in all required fields');
      setLoading(false);
      return;
    }

  try {
    // API call to login endpoint
    const response = await api.post("/auth/login", { // UPDATED to use api instance
      phone,
      password,
    });

    // Extract token and role
    const { token, user: { role: userRole } } = response.data; // Destructure role from user object

    // Save token + login state
    sessionStorage.setItem("token", token); // Changed from localStorage
    sessionStorage.setItem("isLoggedIn", "true"); // Changed from localStorage
    sessionStorage.setItem("role", userRole); // Changed from localStorage

    console.log("‚úÖ Login successful:", { role: userRole });

    // Redirect user by role
    switch (userRole) {
      case "admin":
        navigate("/admin/users");
        break;
      case "customer":
        navigate("/customer");
        break;
      case "employee":
        navigate("/employee");
        break;
      case "customercare":
        navigate("/customercare/requests");
        break;
      default:
        navigate("/");
    }
  } catch (err) {
    console.error("‚ùå Login error:", err);

    if (err.response?.data?.message) {
      setError(err.response.data.message);
    } else {
      setError("Server connection failed. Please try again later.");
    }
  } finally {
    setLoading(false);
  }
};

  return (
    <div style={{
      maxWidth: 420,
      margin: '2rem auto',
      padding: '2rem',
      border: `1px solid var(--border)`,
      borderRadius: 'var(--radius)',
      boxShadow: 'var(--shadow)',
      backgroundColor: 'var(--card)'
    }}>
      <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
        <h2 style={{ color: 'var(--color-primary)', margin: 0 }}>üîê Login</h2>
        <p style={{ color: 'var(--color-textLight)', fontSize: '0.9rem', margin: '0.5rem 0 0 0' }}>
          AL CHAAN MEERA Service Portal
        </p>
        <p style={{ color: 'var(--color-textLight)', fontSize: '0.8rem', margin: '0.3rem 0 0 0' }}>
          Enter your credentials to access the system
        </p>
      </div>

      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '1rem' }}>
          <label style={{ 
            display: 'block', 
            marginBottom: '0.5rem', 
            color: 'var(--color-text)',
            fontWeight: '500' 
          }}>
            üì± Mobile Number: <span style={{ color: 'var(--color-error)' }}>*</span>
          </label>
          <input
            type="tel"
            placeholder="Enter 10-digit mobile number"
            value={phone}
            onChange={e => setPhone(e.target.value)}
            maxLength="10"
            required
            disabled={loading}
            style={{
              width: '100%',
              padding: '0.75rem',
              borderRadius: '6px',
              border: `1px solid var(--border)`,
              outline: 'none',
              fontSize: '14px',
              backgroundColor: loading ? 'var(--color-light)' : 'var(--color-white)'
            }}
          />
        </div>

        <div style={{ marginBottom: '1rem' }}>
          <label style={{ 
            display: 'block', 
            marginBottom: '0.5rem', 
            color: 'var(--color-text)',
            fontWeight: '500' 
          }}>
            üîë Password: <span style={{ color: 'var(--color-error)' }}>*</span>
          </label>
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
            disabled={loading}
            style={{
              width: '100%',
              padding: '0.75rem',
              borderRadius: '6px',
              border: `1px solid var(--border)`,
              outline: 'none',
              fontSize: '14px',
              backgroundColor: loading ? 'var(--color-light)' : 'var(--color-white)'
            }}
          />
        </div>

        <button
          type="submit"
          disabled={loading || !phone || !password}
          style={{
            width: '100%',
            padding: '0.75rem',
            borderRadius: '6px',
            border: 'none',
            backgroundColor: (loading || !phone || !password) ? 'var(--color-textLight)' : 'var(--color-primary)',
            color: 'var(--color-white)',
            fontWeight: 'bold',
            fontSize: '16px',
            cursor: (loading || !phone || !password) ? 'not-allowed' : 'pointer',
            transition: 'background-color 0.3s'
          }}
        >
          {loading ? '‚è≥ Authenticating...' : 'üöÄ Login to System'}
        </button>
      </form>

      {error && (
        <div style={{ 
          color: 'var(--color-error)', 
          marginTop: '1rem', 
          textAlign: 'center',
          padding: '0.75rem',
          backgroundColor: `var(--color-error)15`,
          borderRadius: '6px',
          border: `1px solid var(--color-error)30`
        }}>
          ‚ö†Ô∏è {error}
        </div>
      )}

      <div style={{ 
        marginTop: '1.5rem', 
        padding: '1rem',
        backgroundColor: `var(--color-secondary)10`,
        borderRadius: '6px',
        fontSize: '0.85rem',
        border: `1px solid var(--color-secondary)30`
      }}>


      </div>

      <div style={{ marginTop: '1.5rem', textAlign: 'center' }}>
        <Link 
          to="/register" 
          style={{ 
            color: 'var(--color-primary)', 
            textDecoration: 'none',
            marginRight: '1rem'
          }}
        >
          üìù Register New Account
        </Link>
        <Link 
          to="/forgot-password" 
          style={{ 
            color: 'var(--color-primary)', 
            textDecoration: 'none' 
          }}
        >
          üîë Forgot Password?
        </Link>
      </div>

      <div style={{ 
        marginTop: '1rem', 
        textAlign: 'center', 
        fontSize: '0.8rem', 
        color: 'var(--color-textLight)',
        backgroundColor: `var(--color-light)80`,
        padding: '0.5rem',
        borderRadius: '4px'
      }}>
        üóÑÔ∏è Login credentials are validated against the database
      </div>
    </div>
  );
}

export default LoginPage;
